mapper.select:

select 

from t_i18n
where I18N_KEY = #{i18nKey,jdbcType=VARCHAR}
  and I18N_LANG = #{i18nLang,jdbcType=VARCHAR}
  and DOMAIN_TYPE = #{domainType,jdbcType=VARCHAR}

mapper.delete:

delete from t_i18n
where I18N_KEY = #{i18nKey,jdbcType=VARCHAR}
  and I18N_LANG = #{i18nLang,jdbcType=VARCHAR}
  and DOMAIN_TYPE = #{domainType,jdbcType=VARCHAR}

mapper.update:

update t_i18n


    I18N_CONTENT = #{i18nContent,jdbcType=VARCHAR},


    CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},


    LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=TIMESTAMP},


    LAST_UPDATE_BY = #{lastUpdateBy,jdbcType=VARCHAR},


where I18N_KEY = #{i18nKey,jdbcType=VARCHAR}
  and I18N_LANG = #{i18nLang,jdbcType=VARCHAR}
  and DOMAIN_TYPE = #{domainType,jdbcType=VARCHAR}


update t_i18n
set I18N_CONTENT = #{i18nContent,jdbcType=VARCHAR},
  CREATE_TIME = #{createTime,jdbcType=TIMESTAMP},
  LAST_UPDATE_TIME = #{lastUpdateTime,jdbcType=TIMESTAMP},
  LAST_UPDATE_BY = #{lastUpdateBy,jdbcType=VARCHAR}
where I18N_KEY = #{i18nKey,jdbcType=VARCHAR}
  and I18N_LANG = #{i18nLang,jdbcType=VARCHAR}
  and DOMAIN_TYPE = #{domainType,jdbcType=VARCHAR}
